import React from 'react';
import './About.css'; // Make sure the path matches your setup

const About = () => {
  return (
    <div className="about-container">
      <h1>About Expense Tracker</h1>
      <p className="about-paragraph">
        {Array(300).fill('Expense Tracker').map((text, index) => (
          <span key={index} className="bold-text">{text} </span>
        ))}
      </p>
    </div>
  );
};

export default About;
