import React from 'react';
import './About.css';
const About = () => {
  return (
    <div>
      <h1>About Expense Tracker</h1>
      <p>
        The Expense Tracker application helps you manage and track your expenses efficiently. 
        You can add, edit, and delete expenses, and view your expense history.
      </p>
    </div>
  );
};

export default About;

