import React from 'react';
import './About.css'; // Optional: create a CSS file for the About page styling

const About = () => {
  return (
    <div className="about-container">
      <h1>About Expense Tracker</h1>
      <p>Expense Tracker is a simple and efficient tool to help you keep track of your daily expenses. You can log your expenses, categorize them, and view summaries to manage your budget effectively. With Expense Tracker, you can ensure that you stay on top of your finances and make informed decisions.</p>
    </div>
  );
};

export default About;
