const API_URL = 'http://localhost:3306'; // Replace with your API URL

const apiService = {
  getExpenses: (userId) => axios.get(`${API_URL}/expenses?userId=${userId}`),
  addExpense: (expense) => axios.post(`${API_URL}/expenses`, expense),
  deleteExpense: (id) => axios.delete(`${API_URL}/expenses/${id}`),
};

export default apiService;
