import React from 'react';
// import Navbar from './Navbar'; // Import the Navbar component
// import './Home.css'; // Import your CSS for Home

// const Home = () => {
//   return (
//     <div className="home-container">
//       <Navbar /> {/* Include the Navbar */}
//       <div className="home-content">
//         <h1>Welcome to the Expense Tracker</h1>
//         <p>Your one-stop solution for managing your expenses efficiently.</p>
//       </div>
//     </div>
//   );
// };

const Home = () => {
  const [income, setIncome] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [expenseName, setExpenseName] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');

  const handleAddIncome = () => {
    setIncome(parseInt(income));
  };

  const handleAddExpense = () => {
    setExpenses([...expenses, { name: expenseName, date: expenseDate, amount: parseInt(expenseAmount) }]);
    setExpenseName('');
    setExpenseDate('');
    setExpenseAmount('');
  };

  const handleDeleteExpense = (index) => {
    const newExpenses = expenses.filter((_, i) => i !== index);
    setExpenses(newExpenses);
  };

  const totalExpenses = expenses.reduce((acc, expense) => acc + expense.amount, 0);
  const balance = income - totalExpenses;

  return (
    <div className="Home">
      <h1>Expense Tracker JIIT</h1>
      <h2>Add A New Item</h2>
      <div className="container">
        <label>Income:</label>
        <input
          type="number"
          value={income}
          onChange={(e) => setIncome(e.target.value)}
          className="in-come"
          placeholder="Enter Your Income"
        />
        <button onClick={handleAddIncome} className="addInc">ADD</button>
        <br />
        <div className="nameDiv">
          <label>Name:</label>
          <input
            type="text"
            value={expenseName}
            onChange={(e) => setExpenseName(e.target.value)}
            className="expname"
            placeholder="Where was the expense made?"
          />
        </div>
        <div className="dateAmt">
          <label>Date:</label>
          <input
            type="date"
            value={expenseDate}
            onChange={(e) => setExpenseDate(e.target.value)}
            className="e-date"
          />
          <br />
          <label>Amount:</label>
          <input
            type="number"
            value={expenseAmount}
            onChange={(e) => setExpenseAmount(e.target.value)}
            className="amount"
            placeholder="Enter Amount"
          />
        </div>
        <button onClick={handleAddExpense} className="add-btn">ADD EXPENSES</button>

        <div className="tableList">
          <table>
            <tbody>
              <tr>
                <td>Your Income</td>
                <td>{income}</td>
                <td></td>
              </tr>
              <tr>
                <td>Name</td>
                <td>Date</td>
                <td>Expenses</td>
                <td>Action</td>
              </tr>
              {expenses.map((expense, index) => (
                <tr key={index}>
                  <td>{expense.name}</td>
                  <td>{expense.date}</td>
                  <td>{expense.amount}</td>
                  <td>
                    <button id="delExp" onClick={() => handleDeleteExpense(index)}>Delete</button>
                  </td>
                </tr>
              ))}
              <tr>
                <td>Total Expenses:</td>
                <td>{totalExpenses}</td>
              </tr>
              <tr>
                <td>Balance:</td>
                <td>{balance}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};



export default Home;
