import React, { useState } from 'react';
import { Pie } from 'react-chartjs-2';
import Navbar from './Navbar';
import Footer from './Footer';
import './Home.css';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const Home = () => {
  const [income, setIncome] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [expenseName, setExpenseName] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');

  const handleAddIncome = () => {
    setIncome(parseInt(income));
  };

  const handleAddExpense = () => {
    setExpenses([...expenses, { name: expenseName, date: expenseDate, amount: parseInt(expenseAmount) }]);
    setExpenseName('');
    setExpenseDate('');
    setExpenseAmount('');
  };

  const handleDeleteExpense = (index) => {
    const newExpenses = expenses.filter((_, i) => i !== index);
    setExpenses(newExpenses);
  };

  const totalExpenses = expenses.reduce((acc, expense) => acc + expense.amount, 0);
  const balance = income - totalExpenses;

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(12);
    doc.text('Expense Report', 14, 16);

    doc.autoTable({
      startY: 25,
      head: [['Name', 'Date', 'Amount']],
      body: expenses.map(expense => [expense.name, expense.date, expense.amount]),
      didDrawPage: (data) => {
        doc.text(`Total Expenses: ${totalExpenses}`, 14, data.cursor.y + 10);
        doc.text(`Balance: ${balance}`, 14, data.cursor.y + 20);
      }
    });

    doc.save('expense-report.pdf');
  };

  const data = {
    labels: expenses.map(expense => expense.name),
    datasets: [
      {
        label: 'Expenses',
        data: expenses.map(expense => expense.amount),
        backgroundColor: expenses.map(
          () => `#${Math.floor(Math.random()*16777215).toString(16)}`
        ),
        borderColor: 'rgba(255, 255, 255, 1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="home-container">
      <Navbar />
      <div className="chart-container">
        <Pie data={data} />
      </div>
      <div className="home-content">
        <h1>Expense Tracker</h1>
        <h2>Add A New Item</h2>
        <div className="input-box">
          <div className="form-group">
            <label>Income:</label>
            <input
              type="number"
              value={income}
              onChange={(e) => setIncome(e.target.value)}
              className="in-come"
              placeholder="Enter Your Income"
            />
            <button onClick={handleAddIncome} className="addInc">ADD</button>
          </div>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={expenseName}
              onChange={(e) => setExpenseName(e.target.value)}
              className="expname"
              placeholder="Where was the expense made?"
            />
          </div>
          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              value={expenseDate}
              onChange={(e) => setExpenseDate(e.target.value)}
              className="e-date"
            />
          </div>
          <div className="form-group">
            <label>Amount:</label>
            <input
              type="number"
              value={expenseAmount}
              onChange={(e) => setExpenseAmount(e.target.value)}
              className="amount"
              placeholder="Enter Amount"
            />
          </div>
          <button onClick={handleAddExpense} className="add-btn">ADD EXPENSES</button>
        </div>
        <div className="tableList">
          <table>
            <tbody>
              <tr>
                <td>Your Income</td>
                <td>{income}</td>
                <td></td>
              </tr>
              <tr>
                <td>Name</td>
                <td>Date</td>
                <td>Amount</td>
              </tr>
              {expenses.map((expense, index) => (
                <tr key={index}>
                  <td>{expense.name}</td>
                  <td>{expense.date}</td>
                  <td>{expense.amount}</td>
                  <td>
                    <button id="delExp" onClick={() => handleDeleteExpense(index)}>Delete</button>
                  </td>
                </tr>
              ))}
              <tr>
                <td>Total Expenses:</td>
                <td>{totalExpenses}</td>
                <td></td>
              </tr>
              <tr>
                <td>Balance:</td>
                <td>{balance}</td>
                <td></td>
              </tr>
            </tbody>
          </table>
        </div>
        <button onClick={exportPDF} className="export-btn">Export to PDF</button>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
