import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import './Home.css';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { ThreeDots } from 'react-loader-spinner';

// Register Chart.js components
ChartJS.register(ArcElement, Tooltip, Legend);

const Home = () => {
  const [income, setIncome] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [expenseName, setExpenseName] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expenseTime, setExpenseTime] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');
  const [loading, setLoading] = useState(false);

  // Function to get current time in HH:MM format
  const getCurrentTime = () => {
    const now = new Date();
    const hours = now.getHours().toString().padStart(2, '0');
    const minutes = now.getMinutes().toString().padStart(2, '0');
    return `${hours}:${minutes}`;
  };

  useEffect(() => {
    setExpenseTime(getCurrentTime());
  }, []);

  const handleAddIncome = () => {
    if (income) {
      setIncome(parseInt(income));
      toast.success('Income added successfully!');
    } else {
      toast.error('Please enter a valid income amount.');
    }
  };

  const handleAddExpense = () => {
    if (expenseName && expenseDate && expenseTime && expenseAmount && paymentMethod) {
      setExpenses([...expenses, {
        name: expenseName,
        date: expenseDate,
        time: expenseTime,
        amount: parseInt(expenseAmount),
        paymentMethod
      }]);
      setExpenseName('');
      setExpenseDate('');
      setExpenseTime(getCurrentTime());
      setExpenseAmount('');
      setPaymentMethod('');
      toast.success('Expense added successfully!');
    } else {
      toast.error('Please fill in all fields.');
    }
  };

  const handleDeleteExpense = (index) => {
    const newExpenses = expenses.filter((_, i) => i !== index);
    setExpenses(newExpenses);
    toast.info('Expense deleted.');
  };

  const totalExpenses = expenses.reduce((acc, expense) => acc + expense.amount, 0);
  const balance = income - totalExpenses;

  const exportPDF = () => {
    setLoading(true);
    const doc = new jsPDF();
    doc.setFontSize(12);
    doc.text('Expense Report', 14, 16);

    doc.autoTable({
      startY: 25,
      head: [['Name', 'Date', 'Time', 'Amount', 'Payment Method']],
      body: expenses.map(expense => [expense.name, expense.date, expense.time, expense.amount, expense.paymentMethod]),
      didDrawPage: (data) => {
        doc.text(`Total Expenses: ${totalExpenses}`, 14, data.cursor.y + 10);
        doc.text(`Balance: ${balance}`, 14, data.cursor.y + 20);
      }
    });

    doc.save('expense-report.pdf');
    setLoading(false);
  };

  const pieData = {
    labels: ['Total Income', 'Total Expenses'],
    datasets: [
      {
        data: [income, totalExpenses],
        backgroundColor: ['#36A2EB', '#FF6384'],
        hoverBackgroundColor: ['#36A2EB', '#FF6384'],
      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const label = context.label || '';
            const value = context.raw || 0;
            return `${label}: ${value}`;
          },
        },
      },
    },
  };

  return (
    <div className="home-container">
      <Navbar className="navbar" />
      <div className="home-content">
        <div className="input-box">
          <div className="form-group">
            <label>Income:</label>
            <input
              type="number"
              value={income}
              onChange={(e) => setIncome(e.target.value)}
              className="in-come"
              placeholder="Enter Your Income"
            />
            <button onClick={handleAddIncome} className="addInc">ADD</button>
          </div>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={expenseName}
              onChange={(e) => setExpenseName(e.target.value)}
              className="expname"
              placeholder="Where was the expense made?"
            />
          </div>
          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              value={expenseDate}
              onChange={(e) => setExpenseDate(e.target.value)}
              className="e-date"
            />
          </div>
          <div className="form-group">
            <label>Time:</label>
            <input
              type="time"
              value={expenseTime}
              onChange={(e) => setExpenseTime(e.target.value)}
              className="e-time"
            />
          </div>
          <div className="form-group">
            <label>Amount:</label>
            <input
              type="number"
              value={expenseAmount}
              onChange={(e) => setExpenseAmount(e.target.value)}
              className="amount"
              placeholder="Enter Amount"
            />
          </div>
          <div className="form-group">
            <label>Payment Method:</label>
            <select
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
              className="payment-method"
            >
              <option value="">Select Payment Method</option>
              <option value="Cash">Cash</option>
              <option value="Credit Card">Credit Card</option>
              <option value="Debit Card">Debit Card</option>
              <option value="Bank Transfer">Bank Transfer</option>
            </select>
          </div>
          <button onClick={handleAddExpense} className="add-btn">ADD EXPENSES</button>
        </div>
        <div className="chart-container">
          <Pie data={pieData} options={options} />
        </div>
        <div className="table-container">
          <div className="tableList">
            <table>
              <tbody>
                <tr>
                  <td>Your Income</td>
                  <td>{income}</td>
                  <td></td>
                </tr>
                <tr>
                  <th>Name</th>
                  <th>Date</th>
                  <th>Time</th>
                  <th>Amount</th>
                  <th>Payment Method</th>
                </tr>
                {expenses.map((expense, index) => (
                  <tr key={index}>
                    <td>{expense.name}</td>
                    <td>{expense.date}</td>
                    <td>{expense.time}</td>
                    <td>{expense.amount}</td>
                    <td>{expense.paymentMethod}</td>
                    <td>
                      <button id="delExp" onClick={() => handleDeleteExpense(index)}>Delete</button>
                    </td>
                  </tr>
                ))}
                <tr>
                  <td>Total Expenses:</td>
                  <td>{totalExpenses}</td>
                  <td></td>
                  <td></td>
                  <td></td>
                </tr>
                <tr>
                  <td>Balance:</td>
                  <td>{balance}</td>
                  <td></td>
                  <td></td>
                  <td></td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
        <button onClick={exportPDF} className="export-btn">Export to PDF</button>
        {loading && <ThreeDots color="#00BFFF" height={80} width={80} />}
      </div>
      <Footer />
      <ToastContainer />
    </div>
  );
};

export default Home;
