import axios from 'axios';

const API_URL = 'http://localhost:8080/api/expenses'; // Update this with your backend URL

const addExpense = async (expense) => {
  return await axios.post(API_URL, expense);
};

const getExpenses = async (userId) => {
  return await axios.get(API_URL, { params: { userId } });
};

const deleteExpense = async (id) => {
  return await axios.delete(`${API_URL}/${id}`);
};

export default { addExpense, getExpenses, deleteExpense };
