import React, { useState } from 'react';
import Navbar from './Navbar'; // Import the Navbar component
// import Calculator from './Calculator';
import './Home.css'; // Import your CSS for Home
import jsPDF from 'jspdf';

const Home = () => {
  const [income, setIncome] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [expenseName, setExpenseName] = useState('');
  const [expenseDate, setExpenseDate] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');

  const handleAddIncome = () => {
    setIncome(parseInt(income));
  };

  const handleAddExpense = () => {
    setExpenses([...expenses, { name: expenseName, date: expenseDate, amount: parseInt(expenseAmount) }]);
    setExpenseName('');
    setExpenseDate('');
    setExpenseAmount('');
  };

  const handleDeleteExpense = (index) => {
    const newExpenses = expenses.filter((_, i) => i !== index);
    setExpenses(newExpenses);
  };

  const totalExpenses = expenses.reduce((acc, expense) => acc + expense.amount, 0);
  const balance = income - totalExpenses;

  const exportPDF = () => {
    const doc = new jsPDF();
    
    doc.setFontSize(12);
    doc.text('Expense Report', 14, 16); // Title

    let y = 25; // Y position for text

    // Add income
    doc.text(`Income: ${income}`, 14, y);
    y += 10; // Move to next line

    // Add table headers
    doc.text('Name', 14, y);
    doc.text('Date', 70, y);
    doc.text('Amount', 120, y);
    y += 10;

    // Add expenses
    expenses.forEach((expense) => {
      doc.text(expense.name, 14, y);
      doc.text(expense.date, 70, y);
      doc.text(expense.amount.toString(), 120, y);
      y += 10;
    });

    // Add total expenses
    doc.text(`Total Expenses: ${totalExpenses}`, 14, y);
    y += 10;

    // Add balance
    doc.text(`Balance: ${balance}`, 14, y);

    // Save the PDF
    doc.save('expense-report.pdf');
  };

  return (
    <div className="home-container">
      <Navbar /> {/* Include the Navbar */}
      {/* <Calculator /> */}
      <div className="home-content">
        <h1>Expense Tracker</h1>
        <h2>Add A New Item</h2>
        <div className="input-box">
          <div className="form-group">
            <label>Income:</label>
            <input
              type="number"
              value={income}
              onChange={(e) => setIncome(e.target.value)}
              className="in-come"
              placeholder="Enter Your Income"
            />
            <button onClick={handleAddIncome} className="addInc">ADD</button>
          </div>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={expenseName}
              onChange={(e) => setExpenseName(e.target.value)}
              className="expname"
              placeholder="Where was the expense made?"
            />
          </div>
          <div className="form-group">
            <label>Date:</label>
            <input
              type="date"
              value={expenseDate}
              onChange={(e) => setExpenseDate(e.target.value)}
              className="e-date"
            />
          </div>
          <div className="form-group">
            <label>Amount:</label>
            <input
              type="number"
              value={expenseAmount}
              onChange={(e) => setExpenseAmount(e.target.value)}
              className="amount"
              placeholder="Enter Amount"
            />
          </div>
          <button onClick={handleAddExpense} className="add-btn">ADD EXPENSES</button>
        </div>
        <div className="tableList">
          <table>
            <tbody>
              <tr>
                <td>Your Income</td>
                <td>{income}</td>
                <td></td>
              </tr>
              <tr>
                <td>Name</td>
                <td>Date</td>
                <td>Amount</td>
              </tr>
              {expenses.map((expense, index) => (
                <tr key={index}>
                  <td>{expense.name}</td>
                  <td>{expense.date}</td>
                  <td>{expense.amount}</td>
                  <td>
                    <button id="delExp" onClick={() => handleDeleteExpense(index)}>Delete</button>
                  </td>
                </tr>
              ))}
              <tr>
                <td>Total Expenses:</td>
                <td>{totalExpenses}</td>
                <td></td>
              </tr>
              <tr>
                <td>Balance:</td>
                <td>{balance}</td>
                <td></td>
              </tr>
            </tbody>
          </table>
        </div>
        <button onClick={exportPDF} className="export-btn">Export to PDF</button>
      </div>
    </div>
  );
};

export default Home;
