import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import PieChart from './PieChart';
import Footer from './Footer';
import './Home.css';

const Home = () => {
  const [income, setIncome] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [expenseDate, setExpenseDate] = useState('');
  const [expenseTime, setExpenseTime] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenseMode, setExpenseMode] = useState('');
  const [expenseType, setExpenseType] = useState('');

  useEffect(() => {
    // Fetch initial expenses from backend
    axios.get('http://localhost:3306/api/expenses')
      .then(response => setExpenses(response.data))
      .catch(error => console.error('Error fetching expenses:', error));
  }, []);

  const handleAddIncome = () => {
    setIncome(parseInt(income) || 0);
  };

  const handleAddExpense = () => {
    if (expenseDate && expenseTime && expenseAmount && expenseMode && expenseType) {
      const newExpense = {
        date: expenseDate,
        time: expenseTime,
        amount: parseInt(expenseAmount),
        mode: expenseMode,
        type: expenseType
      };
      
      axios.post('http://localhost:3306/api/expenses', newExpense)
        .then(response => {
          setExpenses([...expenses, response.data]);
          setExpenseDate('');
          setExpenseTime('');
          setExpenseAmount('');
          setExpenseMode('');
          setExpenseType('');
        })
        .catch(error => console.error('Error adding expense:', error));
    }
  };

  const handleDeleteExpense = (id) => {
    axios.delete(`http://localhost:3306/api/expenses/${id}`)
      .then(() => {
        const newExpenses = expenses.filter(expense => expense.id !== id);
        setExpenses(newExpenses);
      })
      .catch(error => console.error('Error deleting expense:', error));
  };

  const totalExpenses = expenses.reduce((acc, expense) => acc + expense.amount, 0);
  const balance = income - totalExpenses;

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(12);
    doc.text('Expense Report', 14, 16);

    doc.autoTable({
      startY: 25,
      head: [['Date', 'Time', 'Amount', 'Mode', 'Type']],
      body: expenses.map(expense => [expense.date, expense.time, expense.amount, expense.mode, expense.type]),
      didDrawPage: (data) => {
        doc.text(`Total Expenses: ${totalExpenses}`, 14, data.cursor.y + 10);
        doc.text(`Balance: ${balance}`, 14, data.cursor.y + 20);
      }
    });

    doc.save('expense-report.pdf');
  };

  return (
    <div className="home-container">
      <div className="home-content">
        {/* Input forms and buttons */}
        <div className="input-box">
          <div className="form-group">
            <label htmlFor="income">Income:</label>
            <input
              type="number"
              id="income"
              value={income}
              onChange={(e) => setIncome(e.target.value)}
              className="input-field"
              placeholder="Enter Your Income"
            />
            <button onClick={handleAddIncome} className="button">Add Income</button>
          </div>
          <div className="form-group">
            <label htmlFor="expenseDate">Date:</label>
            <input
              type="date"
              id="expenseDate"
              value={expenseDate}
              onChange={(e) => setExpenseDate(e.target.value)}
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="expenseTime">Time:</label>
            <input
              type="time"
              id="expenseTime"
              value={expenseTime}
              onChange={(e) => setExpenseTime(e.target.value)}
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="expenseAmount">Amount:</label>
            <input
              type="number"
              id="expenseAmount"
              value={expenseAmount}
              onChange={(e) => setExpenseAmount(e.target.value)}
              className="input-field"
              placeholder="Enter Amount"
            />
          </div>
          <div className="form-group">
            <label htmlFor="expenseMode">Payment Method:</label>
            <select
              id="expenseMode"
              value={expenseMode}
              onChange={(e) => setExpenseMode(e.target.value)}
              className="input-field"
            >
              <option value="">Select Mode</option>
              <option value="Cash">Cash</option>
              <option value="CreditCard">Credit Card</option>
              <option value="DebitCard">Debit Card</option>
              <option value="BankTransfer">Bank Transfer</option>
              <option value="UPI">UPI</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="expenseType">Type:</label>
            <input
              type="text"
              id="expenseType"
              value={expenseType}
              onChange={(e) => setExpenseType(e.target.value)}
              className="input-field"
              placeholder="Enter Type of Expense"
            />
          </div>
          <button onClick={handleAddExpense} className="button">Add Expense</button>
        </div>

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Your Income</th>
                <th>{income}</th>
                <th></th>
              </tr>
              <tr>
                <th>Date</th>
                <th>Time</th>
                <th>Amount</th>
                <th>Mode</th>
                <th>Type</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((expense, index) => (
                <tr key={index}>
                  <td>{expense.date}</td>
                  <td>{expense.time}</td>
                  <td>{expense.amount}</td>
                  <td>{expense.mode}</td>
                  <td>{expense.type}</td>
                  <td>
                    <button className="delete-btn" onClick={() => handleDeleteExpense(expense.id)}>Delete</button>
                  </td>
                </tr>
              ))}
              <tr>
                <td>Total Expenses:</td>
                <td>{totalExpenses}</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>Balance:</td>
                <td>{balance}</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
            </tbody>
          </table>
        </div>
        <button onClick={exportPDF} className="button">Export to PDF</button>
        <PieChart data={expenses} />
      </div>
      <Footer />
    </div>
  );
};

export default Home;
