import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your login logic here
    const username = "Expense Tracker"; // Replace with actual username logic
    navigate('/welcome', { state: { username } }); // Redirect to welcome page with username
  };

  return (
    <div className="auth-container">

      <div className="auth-form-box">
  <h1>Expense Tracker</h1>
        <div className="auth-form">
          <h2 className="auth-title">Login</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            
              />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="forgot-password">
              <a href="/forgot-password"style={{ color: 'white' }}>Forgot password?</a>
            </div>
            <button type="submit" className="auth-button">Login</button>
          </form>
          <div className="register-link">
            Don't have an account? <a href="/register"style={{ color: 'white' }}>Register</a>
          </div>
        </div>
      </div>
      <div className="auth-bg"></div>
    </div>
  );
};

export default Login;
